	p_process_sql_clob => 'declare
    l_component_id varchar2(255);
    l_instance varchar2(255);

    procedure output_json_item (
        p_item_name    in             varchar2,
        p_item_value   in             varchar2
    ) as
    begin
        apex_json.open_object;
        apex_json.write(
            ''id'',
            p_item_name
        );
        apex_json.write(
            ''value'',
            p_item_value,
            true
        );
        apex_json.close_object;
    end output_json_item;

begin
    if apex_application.g_x02 = ''5120'' then
        select
            id
        into l_component_id
        from
            pitss_apex_applications_step_items
        where
            flow_id = :app_id
            and substr(
                item_comment,
                instr(
                    item_comment,
                    '':''
                ) + 1
            ) = apex_application.g_x01;

    end if;

    if apex_application.g_x02 = ''5140'' then
        select
            id
        into l_component_id
        from
