        where
            flow_id = :app_id
            and substr(
                da_event_comment,
                instr(
                    da_event_comment,
                    '':''
                ) + 1
            ) = apex_application.g_x01;

    end if;

    if apex_application.g_x02 = ''5530'' then
        select
            id
        into l_component_id
        from
            pitss_apex_step_processing
        where
            flow_id = :app_id
            and substr(
                process_comment,
                instr(
                    process_comment,
                    '':''
                ) + 1
            ) = apex_application.g_x01;

